#taking out @
jouralistsSimple = journalists.map { |word| word.gsub('@', '') }
#puts jouralistsSimple

puts "Bienvenue, Pour faciliter les choses, je t'ai fait un menu pour te renvoyer les résultats des différents exos."
   sleep 0.5

loop do
   puts ""
   puts "Entre sur ton clavier le numero de la question que tu souhaite voir [1...8], puis tape sur entrer."
   print ">"
   menuChoice = gets.chomp.to_i
    if menuChoice == 1
      puts "" *2
      #how many handles
      journalists.count
      puts "counting : #{journalists.count}"
      sleep 1
    elsif menuChoice == 2
      puts "" *2
      #smallest
      smallest = journalists.min {|x,y| x.size <=> y.size}
      puts "smallest : #{smallest}"
      sleep 1
    elsif menuChoice == 3
      puts "" *2
      #< then 5 charachers
      putFiveCharacter = jouralistsSimple.find_all{|m| m.length == 5}
      puts putFiveCharacter
      sleep 1
    elsif menuChoice == 4
      puts "" *2
      #uppercased
      uppercased = jouralistsSimple.find_all{|str| str[0] != str[0].downcase}
      puts uppercased
      sleep 1
    elsif menuChoice == 5
      #sort by alphabet
      ordered = jouralistsSimple.sort
      puts ordered
      sleep 1
    elsif menuChoice == 6
      puts "" *2
      #order by size
      orderBySize = jouralistsSimple.sort_by {|x| x.length}
      puts orderBySize
      sleep 1
    elsif menuChoice == 7
      #position
      position = journalists.index("@epenser")
      puts position
      sleep 1
    elsif menuChoice == 8
      puts "" *2
            #partition by length manual
            #oneCharacter = jouralistsSimple.partition.find_all {|m| m.length == 1}
            #puts "#{oneCharacter}"

      #partition by length auto
      x = 1
      i = 0
      loop do
        character = "character#{x}"
        parti = jouralistsSimple.partition.find_all {|m| m.length == x}
        puts "#{character} : " "#{parti}"
        x=x+1
        i = i+1
        if i == 20
        break
        sleep 1
        end
      end
    else
      abort("We hope to see you again.")
    end 
end

